<script>
import Map from "./Map";
import image1 from "../assets/gallery/HYW09498.jpg"
import image2 from "../assets/gallery/HYW09305.jpg"
import image3 from "../assets/gallery/HYW09384.jpg"
import image4 from "../assets/gallery/HYW09470.jpg"
import image5 from "../assets/gallery/HYW09542.jpg"
import image6 from "../assets/gallery/HYW09536.jpg"


export default {
  name: "Main",
  components: {
    Map
  },
  data() {
    return {
      btnClick: false,
      btnClick1: false,
      curPos: 0,
      postion: 0,
      start_x: 0,
      end_x: 0,
      IMAGE_WIDTH: 0,
      images: null,
      imageUrls: [
        image3,
        image1,
        image2,
        image4,
        image5,
        image6
      ],
    };
  },
  computed: {
    getImageWidth: () => {
      const imgWidth = document.querySelector(".images").offsetWidth;
      return imgWidth;
    },
  },
  mounted: function () {
    this.IMAGE_WIDTH = this.getImageWidth;
    this.images = document.querySelector(".images");
    this.images.addEventListener("touchstart", this.touch_start);
    this.images.addEventListener("touchend", this.touch_end);
  },
  methods: {
    kakaoLink() {
      window.Kakao.Link.sendDefault({
            objectType: 'feed',
            content: {
              title: '김재욱 & 김은희 결혼합니다.',
              description: '2022년 11월 27일 오전 10시 30분 \n노비아갈라 전자관 5층 보타닉가든홀',
              imageUrl:
                  'http://drive.google.com/uc?export=view&id=1-CweZ1mIoQjjSLFbgdU0ElvKp8sfz3ns',
              link: {
                mobileWebUrl: 'https://ukkidoki.github.io/wedding/',
                webUrl: 'https://ukkidoki.github.io/wedding/',
              },
            },
            buttons: [
              {
                title: '청첩장 보기',
                link: {
                  mobileWebUrl: 'https://ukkidoki.github.io/wedding/',
                  webUrl: 'https://ukkidoki.github.io/wedding/',
                },
              }
            ]
          }
      )
    },
    showPay() {
      this.btnClick != true ? this.btnClick = true : this.btnClick = false;
    }
    ,
    showPay1() {
      this.btnClick1 != true ? this.btnClick1 = true : this.btnClick1 = false;
    }
    ,
    prev() {
      if (this.curPos > 0) {
        this.postion += this.IMAGE_WIDTH;
        this.images.style.transform = `translateX(${this.postion}px)`;
        this.curPos = this.curPos - 1;
      }
    }
    ,
    next() {
      if (this.curPos < this.imageUrls.length - 1) {
        this.postion -= this.IMAGE_WIDTH;
        this.images.style.transform = `translateX(${this.postion}px)`;
        this.curPos = this.curPos + 1;
      }
    }
    ,

    touch_start(event) {
      this.start_x = event.touches[0].pageX;
    }
    ,

    touch_end(event) {
      this.end_x = event.changedTouches[0].pageX;
      if (this.start_x > this.end_x) this.next();
      else this.prev();
    }
    ,
  },
};

</script>
